#include <iostream>
#include <vector>

using namespace std;

bool isPrime(int num) {
  if (num <= 1)
    return 0;
  if (num == 2 || num == 3) {
    return 1;
  }
  for (int i = 2; i < num / 2 + 1; i++) {
    if (!(num % i)) {
      return 0;
    }
  }
  return 1;
}

bool isAdditivePrime(int num) {
  if (!isPrime(num)) {
    return false;
  }
  int sum = 0;
  while (num > 0) {
    sum += num % 10;
    num /= 10;
  }
  return isPrime(sum);
}

